package tsgenerated

import (
	"encoding/hex"
	"testing"

	"github.com/bsv-blockchain/go-sdk/chainhash"
	"github.com/bsv-blockchain/go-sdk/transaction"
	"github.com/stretchr/testify/require"
)

/*
BeefToInternalize contains a transaction on testnet that sends funds to testusers.Alice (identityKey: 0320bbfb879bbd6761ecd2962badbb41ba9d60ca88327d78b07ae7141af6b6c810)
Address: mvpQZzaN5wMBeMtDjvDTLy8HJd7boN5hf2
DerivationPrefix: Pr==
DerivationSuffix: Su==
SenderIdentityKey: fixtures.AnyoneIdentityKey (0279be667ef9dcbbac55a06295ce870b07029bfcdb2dce28d959f2815b16f81798)
OutputIndex: 0

https://test.whatsonchain.com/tx/db8ee15998a415f69144483cf9a755d05f2a7c44e3569c8fe750720a26f90fe7
*/
const BeefToInternalize = ""
const BeefToInternalizeTxID = "db8ee15998a415f69144483cf9a755d05f2a7c44e3569c8fe750720a26f90fe7"

func AtomicBeefToInternalize(t *testing.T) []byte {
	beefBytes, err := hex.DecodeString(BeefToInternalize)
	require.NoError(t, err)

	beef, err := transaction.NewBeefFromBytes(beefBytes)
	require.NoError(t, err)

	txIDHash, err := chainhash.NewHashFromHex(BeefToInternalizeTxID)
	require.NoError(t, err)

	atomic, err := beef.AtomicBytes(txIDHash)
	require.NoError(t, err)

	return atomic
}
