package tsgenerated

import (
	"github.com/bsv-blockchain/go-sdk/transaction"
	"github.com/stretchr/testify/require"
	"testing"
)

// SignedTransactionHex is a rawTx of a transaction created from instructions from create_action_result.json
// Sender: testusers.Alice
// Receiver: testusers.Bob
const SignedTransactionHex = ""

func SignedTransaction(t *testing.T) *transaction.Transaction {
	t.Helper()
	tx, err := transaction.NewTransactionFromHex(SignedTransactionHex)
	require.NoError(t, err)
	return tx
}
